import React, { useEffect } from 'react';
import dlocal from 'dloc'
const MyComponent = () => {
  useEffect(() => {
    const dlocalInstance = dlocal("b41fb681-93eb-46fc-a6bb-24aa51aeb6d6");
    const fields = dlocalInstance.fields({
      locale: 'en',
      country: 'NG',
      fonts: [{
        cssSrc: 'https://rsms.me/inter/inter-ui.css'
      }]
    });

    const card = fields.create('card', {
      style: {
        base: {
          fontSize: "16px",
          fontFamily: "'Inter UI medium', sans-serif",
          lineHeight: '18px',
          fontSmoothing: 'antialiased',
          fontWeight: '500',
          color: "#32325d",
          '::placeholder': {
            color: "#aab7c4"
          },
          iconColor: "#adbfd3"
        }
      }
    });

    card.mount(document.getElementById('card-form'));

    card.addEventListener('change', function (event) {
      const displayError = document.getElementById('error-field');
      if (event.error) {
        displayError.textContent = event.error.message;
      } else {
        displayError.textContent = '';
      }
    });

    const form = document.getElementById('payment-form');
    form.addEventListener('submit', function (event) {
      event.preventDefault();
      const cardHolderName = document.getElementById('card-holder').value;
      dlocalInstance.createToken(card, { name: cardHolderName })
        .then(function (result) {
          dlocalTokenHandler(result.token);
        })
        .catch((result) => {
          if (result.error) {
            const errorField = document.getElementById('error-field');
            errorField.textContent = result.error.message;
          }
        });
    });

    return () => {
      card.destroy();
    };
  }, []);

  const dlocalTokenHandler = (token) => {
    const form = document.getElementById('payment-form');
    const tokenInput = document.createElement('input');
    tokenInput.setAttribute('type', 'hidden');
    tokenInput.setAttribute('name', 'dlocalToken');
    tokenInput.setAttribute('value', token);
    form.appendChild(tokenInput);

    form.submit();
  };

  return (
    <div className="row">
      <div className="col-75">
        <h2>SmartFields dLocal Integration</h2>
        <div className="container">
          <form action="auth3ds.php" method="post" id="payment-form">
            {/* ... rest of the HTML */}
          </form>
        </div>
      </div>
    </div>
  );
};

export default MyComponent;
