import React, { useEffect } from 'react';
//import dlocal from 'parent';
//import * as dlocal from 'https://static-sandbox.dlocal.com/modules/fields/4.1.0/parent.js';

const smartFieldsConfig = {
  url: "https://js-sandbox.dlocal.com",
  country: "BR",
  api_key: "7a0f1cc1-b17b-4040-9032-f972cf599243",
};

export function addFields(loadFields, smartFieldsConfig) {
  var s = document.createElement("script");
  s.setAttribute("src", smartFieldsConfig.url);
  //s.onload = loadFields;
  document.body.appendChild(s);
}

const MyComponent = () => {
  useEffect(() => {
    addFields(smartFieldsConfig);
    const dlocalInstance = dlocal("b41fb681-93eb-46fc-a6bb-24aa51aeb6d6");
    const fields = dlocalInstance.fields({
      locale: 'en',
      country: 'NG',
      fonts: [{
        cssSrc: 'https://rsms.me/inter/inter-ui.css'
      }]
    });

    const card = fields.create('card', {
      style: {
        base: {
          fontSize: "16px",
          fontFamily: "'Inter UI medium', sans-serif",
          lineHeight: '18px',
          fontSmoothing: 'antialiased',
          fontWeight: '500',
          color: "#32325d",
          '::placeholder': {
            color: "#aab7c4"
          },
          iconColor: "#adbfd3"
        }
      }
    });

    card.mount(document.getElementById('card-form'));

    card.addEventListener('change', function (event) {
      const displayError = document.getElementById('error-field');
      if (event.error) {
        displayError.textContent = event.error.message;
      } else {
        displayError.textContent = '';
      }
    });

    const form = document.getElementById('payment-form');
    form.addEventListener('submit', function (event) {
      event.preventDefault();
      const cardHolderName = document.getElementById('card-holder').value;
      dlocalInstance.createToken(card, { name: cardHolderName })
        .then(function (result) {
          dlocalTokenHandler(result.token);
        })
        .catch((result) => {
          if (result.error) {
            const errorField = document.getElementById('error-field');
            errorField.textContent = result.error.message;
          }
        });
    });

    return () => {
      form();
    };
  }, []);

  const dlocalTokenHandler = (token) => {
    const form = document.getElementById('payment-form');
    const tokenInput = document.createElement('input');
    tokenInput.setAttribute('type', 'hidden');
    tokenInput.setAttribute('name', 'dlocalToken');
    tokenInput.setAttribute('value', token);
    form.appendChild(tokenInput);

    form.submit();
  };

  return (
    <div class="row">
      <div class="col-75">
        <h2>SmartFields dLocal Integration</h2>
        <div class="container">
          <form action="auth3ds.php" method="post" id="payment-form">
            <div class="row">
              <div class="col-50">
                <h3>Billing Information</h3>
                <label for="card-holder"><i class="fa fa-user"></i> Full Name</label>
                <input type="text" id="card-holder" name="card-holder" placeholder="John M. Doe" value="John Doe"></input>
                <div class="row">
                  <div class="col-50">
                    <label for="country">Country</label>
                    <input type="text" id="country" name="country" placeholder="CO" value="CO"></input>
                  </div>
                  <div class="col-50">
                    <label for="city">City</label>
                    <input type="text" id="city" name="city" placeholder="BG" value="Bogota"></input>
                  </div>
                </div>
              </div>

              <div class="col-50">
                <h3>Payment</h3>
                <label for="fname">Accepted Cards</label>
                <div class="icon-container">
                  <i class="fa fa-cc-visa" style={{ color: "navy" }}></i>
                  <i class="fa fa-cc-amex" style={{ color: "blue" }}></i>
                  <i class="fa fa-cc-mastercard" style={{ color: "red" }}></i>
                  <i class="fa fa-cc-discover" style={{ color: "orange" }}></i>
                </div>
                <label for="cname">Payment Information</label>
                <div id="card-form"></div>
              </div>

            </div>
            <label id="error-field"></label>
            <input type="submit" value="Continue to checkout" class="btn"></input>
          </form>
        </div>
      </div>
    </div>
  );
};

export default MyComponent;
